INSERT INTO `anthorintro`(`auid`, `auname`, `intro`) VALUES (71, '莫提默·艾德勒 查尔斯·范多伦', '查尔斯·范多伦（1926－ ）先曾任美国哥伦比亚大学教授。后因故（NBC的一个电视丑闻）离任，和（哲学研究院多年同事）艾德勒一起工作。');
INSERT INTO `anthorintro`(`auid`, `auname`, `intro`) VALUES (72, '尹建莉', '尹建莉，教育学者，作家。北京师范大学教育硕士，“尹建莉父母学堂”首席导师。 著作《好妈妈胜过好老师》2009年出版。2014年出版《最美的教育最简单》，2016年出版《好妈妈胜过好老师2：自由的孩子最自觉》，2018年出版《从“小”读到“大”》。2015年出版译作《小王子》。 2015年创办“尹建莉父母学堂”。');
INSERT INTO `anthorintro`(`auid`, `auname`, `intro`) VALUES (73, '威廉·斯蒂克斯鲁德 奈德·约翰逊', NULL);
INSERT INTO `anthorintro`(`auid`, `auname`, `intro`) VALUES (74, '樊登', NULL);
