SET NAMES utf8;
DROP DATABASE IF EXISTS weixinread;
CREATE DATABASE weixinread charset=utf8;
USE weixinread;
#创建用户表
CREATE TABLE user(
    uid INT PRIMARY KEY AUTO_INCREMENT,   #用户id
    uname VARCHAR(16),   #用户名
    upwd VARCHAR(16),    #用户密码
    sex INT,             #用户性别                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
    avatar VARCHAR(32),  #用户头像
    shelfid INT,         #用户评论id
    bid INT,             #收藏的书籍id
    phone VARCHAR(16),           #用户手机号
    email VARCHAR(32)    #用户邮箱
);


#创建作者简介表
CREATE TABLE anthorintro(
    auid int PRIMARY KEY AUTO_INCREMENT,    #作者id
    auname varchar(16),   #作者名
    intro text            #作者简介
);


#创建书籍详情表
CREATE TABLE bookdetails(
    bid int PRIMARY KEY AUTO_INCREMENT,             #书籍id
    bname varchar(32),   #书籍名
    author text,         #作者名
    fid int,             #书籍类别id   
    introduction text,   #书籍简介  
    recommended int,     #推荐值   
    pic varchar(32)      #书籍封面图片
);



#创建评论详情表
CREATE TABLE comment(
    pid int PRIMARY KEY AUTO_INCREMENT,             #评论id
    bid int,             #书籍id
    uname varchar(32),   #用户名
    uid int,             #用户id
    details text         #评论详情
);


#创建出版详情表
CREATE TABLE publish(
    pubid int PRIMARY KEY AUTO_INCREMENT,                 #出版id
    bid int,                   #书籍id
    pubname varchar(16),       #出版社名称
    pubtime varchar(16),       #出版时间
    pubdetail text,            #出版详情
    translator varchar(16),    #译者
    bname varchar(16)
);




#创建书籍类别表
CREATE TABLE bookfamilys(
    fid int PRIMARY KEY AUTO_INCREMENT,             #书籍类别id
    tname varchar(16),   #书籍类别名
    total int            #书籍类别数量
);


#创建书籍内容表
CREATE TABLE bookcontain(
    tid int PRIMARY KEY AUTO_INCREMENT,     #书籍章节id
    contain_name varchar(255),       #章节名
    contain text #书籍内容
);

#创建书架内容表
CREATE TABLE shujia(
    bsid int PRIMARY KEY AUTO_INCREMENT,     #书架id
    uid int,       #用户id
    bid int     #书籍id
);

--------------


